// Generador de partÃ­culas
// Para usar, establecer el sprite en .partCompo, configurar e invocar iniciar()
function Particulas(conf) {
	this.x = conf.x || 0; // posiciÃ³n del origen de las partÃ­culas
	this.y = conf.y || 0;
	this.z = conf.z || 1;
	this.vx = conf.vx || 0; // Velocidad x de las partÃ­culas por defecto
	this.atenuacion = conf.atenuacion || 12; // Reduce la rapidez de movimiento de las partÃ­culas
	this.magnitud = conf.magnitud || 20; // Medida de la altura que alcanzan las partÃ­culas
	this.duracion = conf.duracion || 35; // DuraciÃ³n en frames de las partÃ­culas antes de destruirse
	this.deltaDura = conf.deltaDura || 5; // VariaciÃ³n aleatoria en la duraciÃ³n
	this.deltaOriX = conf.deltaOriX || 0; // Longitud de variaciÃ³n aleatoria de la coord. x del origen (en sentido +X)
	this.deltaOriY = conf.deltaOriY || 0;
	this.deltaVx = conf.deltaVx || 2; // VariaciÃ³n aleatoria en la velocidad x
	this.periodo = conf.periodo || 200; // tiempo en ms. para generar la siguiente partÃ­cula
	this.numParticulas = conf.numParticulas || 16; // contador de partÃ­culas por generar. Luego se apaga el generador
	this.objInterval = conf.objInterval || null; // objeto del setInterval()
	this.f_crear = conf.f_crear || null; // funciÃ³n a invocar cada vez que se cree una partÃ­cula. Recibe la entidad como parametro
	this.componentes = conf.componentes || ""; // Componentes adicionales de cada partÃ­cula (miSprite, SpriteAnimation, etc.)
	
	this.cuentaParticulas = 0;
}

// InicializaciÃ³n
Particulas.prototype.init = function() {
	
};

// Generates a random float between 2 values
Particulas.prototype.random = function(min, max) {
	return Math.random() * (max - min + 1) + min;
};

// Generates a random integer between 2 values
Particulas.prototype.randomInt = function(min, max) {
	return Math.floor(Math.random() * (max - min + 1) + min);
};

// Inicia el bucle de generaciÃ³n de partÃ­culas
Particulas.prototype.iniciar = function() {
	var self = this;
	this.frame = 0;
	this.cuentaParticulas = 0;
	
	if (self.cuentaParticulas < self.numParticulas) {
		this.objInterval = window.setInterval(function() {
			if (self.cuentaParticulas >= self.numParticulas) {
				clearInterval(self.objInterval);
			}
			else {
				self.crear(self);
				self.cuentaParticulas++;
			}
		}, this.periodo);
	}
};

// Dispara una partÃ­cula desde el origen
Particulas.prototype.crear = function(self) {
	var e_part = Crafty.e("2D, Canvas, Particula, " + this.componentes);
	
	// Establecemos atributos iniciales
	if (self.deltaOriX === 0) {
		e_part.x = self.x;
	} else {
		e_part.x = self.x + self.random(0, self.deltaOriX);
	}
	
	if (self.deltaOriY === 0) {
		e_part.yIni = self.y;
	} else {
		e_part.yIni = self.y + self.random(0, self.deltaOriY);
	}
	
	e_part.z = self.z;
	
	if (self.deltaVx === 0) {
		e_part.vx = self.vx;
	} else {
		e_part.vx = self.random(self.vx - self.deltaVx, self.vx + self.deltaVx);
	}
	
	e_part.atenuacion = self.atenuacion;
	e_part.magnitud = self.magnitud;
	if (this.deltaDura === 0) {
		e_part.duracion = self.duracion;
	} else {
		e_part.duracion = self.randomInt(self.duracion - self.deltaDura, self.duracion + self.deltaDura);
	}
	
	if (this.f_crear) {
		this.f_crear(e_part);
	}
};
